import { useState } from 'react';
import { login } from '../services/user';
import { toast } from 'react-toastify';
import { Link, useNavigate } from 'react-router-dom';
import jwt_decode from 'jwt-decode';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  // get navigate function reference
  const navigate = useNavigate();

  const onLogin = async () => {
    // client side validation
    if (email.length === 0) {
      toast.warning('Please enter email');
    } else if (password.length === 0) {
      toast.warning('Please enter password');
    } else {
      // call the login api and get the result
      const result = await login(email, password);

      // check if the result is successful
      if (result.status === 'success') {
        // read the token from data
        const user = result.user;
        //const token = data.token;

        // cache user token
        //sessionStorage.setItem('token', token);

        // decode the token to get the user role
        //const decodedToken = jwt_decode(token);
        const userRole = user.role; // Adjust this according to your token structure

        toast.success('Welcome to the application');

        // redirect based on user role
        if (userRole === "ADMIN") {
          navigate('/admin/profile');
        } else if (userRole === 'customer') {
          navigate('/customer/profile');
        } else if (userRole === 'deliveryAgent') {
          navigate('/delivery/profile');
        } else if (userRole === 'warehouseManager') {
          navigate('/warehouse/profile');
        } else {
          toast.error('Unknown user role');
        }
      } else {
        toast.error(result.error);
      }
    }
  };

  return (
    <div>
      <h2 className='heading'>Login</h2>
      <div className='row'>
        <div className='col'></div>
        <div className='col'>
          <div className='mb-3'>
            <label htmlFor=''>Email</label>
            <input
              onChange={(e) => setEmail(e.target.value)}
              type='text'
              className='form-control'
            />
          </div>
          <div className='mb-3'>
            <label htmlFor=''>Password</label>
            <input
              onChange={(e) => setPassword(e.target.value)}
              type='password'
              className='form-control'
            />
          </div>
          <div className='mb-3'>
            <div>
              Don't have an account? <Link to='/register'>Register here</Link>
            </div>
            <div>
              Forgot password?{' '}
              <button className='btn btn-link'>Reset here.</button>
            </div>
            <button onClick={onLogin} className='btn btn-success mt-3'>
              Login
            </button>
          </div>
        </div>
        <div className='col'></div>
      </div>
    </div>
  );
}

export default Login;
